import React, {Component} from "react";
export class SimpleButton extends Component{

    constructor(props){
        super(props);
        this.state = {
            counter: 0,
            hasButtonBeenClicked: false,
        };
    }

    handleClick = () =>{
        this.setState({
            counter : this.state.counter + 1,
            hasButtonBeenClicked: true,
        });
    }

    render(){
        return(
        <button
            className={`btn btn-${this.props.theme} btn-sm m-1`}
            onClick={this.handleClick}>
            {this.props.text} 
            {this.state.hasButtonBeenClicked &&(<div>Clicked: {this.state.counter}</div>) }
        </button>
        );
    }
}
