import react from "react";
export default class SimpleButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: 0,
      hasButtonBeenClicked: false,
    };
  }

  handleClick = () => {
    this.setState({
      counter: this.state.counter + 1,
      hasButtonBeenClicked: true,
    });
  };
}
