import React from "react";
import reactDOM from "react-dom";
import "./Styles/index.css";
import "bootstrap/dist/css/bootstrap.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import { faTrashAlt } from "@fortawesome/free-solid-svg-icons";
//import { library } from "@fortawesome/fontawesome-svg-core";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import { faRetweet } from "@fortawesome/free-solid-svg-icons";
// import { faEllipsisV } from "@fortawesome/free-solid-svg-icons";
// import { faEllipsisH } from "@fortawesome/free-solid-svg-icons";
// import { faShare } from "@fortawesome/free-solid-svg-icons";
// import { faThumbsUp } from "@fortawesome/free-solid-svg-icons";
// import { faUpload } from "@fortawesome/free-solid-svg-icons";
import { faComment } from "@fortawesome/free-solid-svg-icons";
import { faReply } from "@fortawesome/free-solid-svg-icons";
import { SimpleButton } from "./SimpleButton";

function Tweet() {
  return (
    <div className="tweet">
      <Avatar />
      <div className="content">
        <NameWithHandle />
        <Message />
        <div className="buttons">
          <ReplyButton />
          <RetweetButton />
          <CommentButton />
          <LikeButton />
        </div>
      </div>
    </div>
  );
}

const ReplyButton = () => (
  <span>
    <button
      onClick={this.handleClick}
      className="like-count btn btn-sm btn-primary m-2 col-ml-auto"
    >
      <FontAwesomeIcon icon={faReply} />
      <SimpleButton
        {...(this.state.hasButtonBeenClicked && (
          <div>{this.state.counter}</div>
        ))}
      />
    </button>
  </span>
);

const RetweetButton = () => (
  <span>
    <button className="like-count btn btn-sm btn-dark m-2 col-ml-auto">
      <FontAwesomeIcon icon={faRetweet} />
    </button>
  </span>
);

const CommentButton = () => (
  <span>
    <button className="like-count btn btn-sm btn-success m-2 col-ml-auto">
      <FontAwesomeIcon icon={faComment} />
    </button>
  </span>
);

const LikeButton = () => (
  <span>
    <button className="like-count btn btn-sm btn-info m-2 col-ml-auto">
      <FontAwesomeIcon icon={faHeart} />
    </button>
  </span>
);

function Avatar() {
  let url = `https://s.gravatar.com/avatar/3382688c70a64007e1b4791f18b6799f?s=80`;
  return <img src={url} className="avatar" alt="avatar" />;
}
function Message() {
  return (
    <div className="mesage">
      coucou ! Un petit message pour vérifier que tu passes une très belle
      journée ! J’espère que ta journée est aussi belle que la mienne !
    </div>
  );
}

function NameWithHandle() {
  return (
    <span className="name-with-handle">
      <span className="name">Bouyagui</span>
      <span className="handle">@Bouyaguidiakite</span>
    </span>
  );
}

